using System.Numerics;

using Play.Ber.Codecs;
using Play.Ber.DataObjects;
using Play.Ber.Exceptions;
using Play.Ber.Identifiers;
using Play.Codecs;

namespace Play.Emv.Ber.DataElements;

/// <summary>
///     Generated by the issuer using the private key that corresponds to the public key authenticated in the Issuer Public
///     Key Certificate.It is a digital signature covering critical ICC-resident static data elements, as described in
///     section 5.4.
/// </summary>
public record SignedStaticApplicationData : DataElement<BigInteger>, IEqualityComparer<SignedStaticApplicationData>
{
    #region Static Metadata

    public static readonly PlayEncodingId EncodingId = BinaryCodec.EncodingId;
    public static readonly Tag Tag = 0x93;

    #endregion

    #region Constructor

    public SignedStaticApplicationData(BigInteger value) : base(value)
    { }

    #endregion

    #region Serialization

    public static SignedStaticApplicationData Decode(ReadOnlyMemory<byte> value) => Decode(value.Span);

    /// <exception cref="InvalidOperationException"></exception>
    /// <exception cref="BerParsingException"></exception>
    public static SignedStaticApplicationData Decode(ReadOnlySpan<byte> value) => new(new BigInteger(value));

    public override PrimitiveValue Decode(TagLengthValue value) => Decode(value.EncodeValue().AsSpan());
    public override byte[] EncodeValue(BerCodec codec) => codec.EncodeValue(EncodingId, _Value);
    public override byte[] EncodeValue(BerCodec codec, int length) => codec.EncodeValue(EncodingId, _Value, length);

    #endregion

    #region Equality

    public bool Equals(SignedStaticApplicationData? x, SignedStaticApplicationData? y)
    {
        if (x is null)
            return y is null;

        if (y is null)
            return false;

        return x.Equals(y);
    }

    public int GetHashCode(SignedStaticApplicationData obj) => obj.GetHashCode();

    #endregion

    #region Instance Members

    public override PlayEncodingId GetEncodingId() => EncodingId;
    public override Tag GetTag() => Tag;
    public override ushort GetValueByteCount(BerCodec codec) => codec.GetByteCount(GetEncodingId(), _Value);

    #endregion
}